package ProgramacionMultiproceso.ejemplo5;

import java.io.BufferedReader;
import java.io.IOException;
import java.io.PrintWriter;

public class ProcesadorFichero {

    // Dado un fichero de entrada y una letra
    // contamos cuantas veces aparece dicha letra
    // y dejamos el recuento en un fichero de salida
    public static void hacerRecuento(String fichEntrada, String letra, String fichSalida) throws IOException {

        // Usar comparación insensible a mayúsculas/minúsculas
        char letraPasada = Character.toLowerCase(letra.charAt(0));

        int totalVocales = 0;
        // Abrimos recursos con try-with-resources para garantizar cierre
        try (BufferedReader br = Utiles.getBufferedReader(fichEntrada);
             PrintWriter pw = Utiles.getPrintWriter(fichSalida)) {

            String lineaLeida = br.readLine();

            // Mientras no queden líneas....
            while (lineaLeida != null) {
                // ...recorremos la linea...
                for (int i = 0; i < lineaLeida.length(); i++) {
                    char letraLeida = Character.toLowerCase(lineaLeida.charAt(i));
                    // incrementamos el contador
                    if (letraLeida == letraPasada) {
                        totalVocales++;
                    }
                }
                // Pasamos a la siguiente linea
                lineaLeida = br.readLine();
            }

            // Escribimos el total de vocales en el fichero de salida
            pw.println(totalVocales);
            pw.flush();
        }
        // fin del método hacerRecuento
    }
}

